mapper.update:

     UPDATE EMPLOYEE 
        SET EMP_NAME=#{empName}
          , EMP_PHONE=#{empPhone}
          , EMP_EMAIL=#{empEmail}
          , EMP_ADDR=#{empAddr}
          , EMP_DETAIL_ADDR=#{empDetailAddr} 
          
          , ORI_FILENAME=#{oriFileName}
          , DEST_FILENAME=#{destFileName}
          
          , EMP_DATE=(select emp_date from employee where EMP_NO=#{empNo})
       WHERE EMP_NO=#{empNo}

UPDATE EMPLOYEE 
SET 
    DEPT_CODE=#{deptCode},
    JOB_CODE=#{jobCode},
    WORK_YN=#{workYn}
WHERE EMP_NO=#{empNo}
