mapper.insert:
INSERT INTO SCHEDULE 
( 
	SCHEDULE_NO
	,CATEGORY
	,SKD_TITLE
	,SKD_CONTENT
	,EMP_NO
	,SKD_START
	,SKD_END 
	,SKD_TYPE_CD
) 
VALUES 
( 
	SEQ_SCHEDULE_NO.NEXTVAL 
	, #{category} 
	, #{skdTitle}
	, #{skdContent}
	, #{empNo} 
	, #{skdStart}
	, #{skdEnd}
	,#{skdTypeCd}
)

INSERT INTO SCHEDULE 
	( 
		SCHEDULE_NO
		,CATEGORY
		,SKD_TITLE
		,SKD_CONTENT
		,EMP_NO
		,SKD_START
		,SKD_END 
	) 
	VALUES 
	( 
		SEQ_SCHEDULE_NO.NEXTVAL 
		, #{category} 
		, #{skdTitle}
		, #{skdContent}
		, #{empNo} 
		, #{skdStart}
		, #{skdEnd}
	)

mapper.update:
UPDATE schedule
	SET
		SKD_TITLE = #{skdTitle}
		, CATEGORY = #{category}
		, SKD_CONTENT = #{skdContent}
		, SKD_START = #{skdStart}
		, SKD_END = #{skdEnd}
		, MOD_DTIME = SYSDATE
	WHERE SCHEDULE_NO = #{scheduleNo}

mapper.select:
SELECT STAR
FROM schedule
WHERE SCHEDULE_NO = #{scheduleNo}

mapper.update:
UPDATE schedule
